import axios from "axios";

const LandingPage = async ({ currentUser }) => {
  console.log(currentUser);
  return <h1>Landing Page</h1>;
};

LandingPage.getInitialProps = async () => {
  if (typeof window === "undefined") {
    // we are on the server!
    // requests should be made to http://SERVICENAME.NAMESPACE.svc.cluster.local//.....
    const { data } = await axios.get(
      "http://ingress-nginx.ingress-nginx.svc.cluster.local/api/users/currentuser",
      {
        headers: {
          Host: "ticketing.dev"
        }
      }
    );
    return data;
  } else {
    // we are on the browser!
    // requests can be made to the url
    const { data } = await axios.get("/api/users/currentuser");

    return data;
  }
};

export default LandingPage;
